import React, { Component } from 'react';
import Todos from './Todo';
import './App.css';


class App extends Component {
    state = {
        todos: [
            { id: 1, content: 'buy Banana' },
            { id: 2, content: 'play violin' },
            { id: 3, content: 'play piano' },
            { id: 4, content: 'do workouts' },
            { id: 5, content: 'do homeworks' },
            { id: 6, content: 'play violin' },
        ]
    }
    deleteTodo = (id) => {
        const todos = this.state.todos.filter(todo => {
            return todo.id !== id
        });
        this.setState({
            todos
        });
    }
    render() {
        return (
            <div className='App-header'>
                <h1 className="center yellow-text">Todo's</h1>
                <Todos todos={this.state.todos} deleteTodo={this.deleteTodo} />
            </div>
        );
    }
}

export default App;
